refentry.refsect1:
  &reftitle.description;

&style.oop;

   public objectfalsemysqli_result::fetch_field_direct
   intindex


&style.procedural;

   objectfalsemysqli_fetch_field_direct
   mysqli_resultresult
   intindex



   Devuelve un objeto que contiene los metadatos
   de un campo en el conjunto de resultados especificado.

&reftitle.parameters;



  &mysqli.result.description;

   index



     El número del campo. Este valor debe estar en el intervalo
     0 a número de campos - 1.

  &reftitle.examples;

   &style.oop;


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Verificación de la conexión */
if (mysqli_connect_errno()) {
    printf("Fallo en la conexión: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Name LIMIT 5";

if ($result = $mysqli->query($query)) {

    /* Obtener información del campo para la columna 'SurfaceArea' */
    $finfo = $result->fetch_field_direct(1);

    printf("Nombre      : %s\n", $finfo->name);
    printf("Tabla       : %s\n", $finfo->table);
    printf("Tamaño máx : %d\n", $finfo->max_length);
    printf("Flags       : %d\n", $finfo->flags);
    printf("Tipo        : %d\n", $finfo->type);

    $result->close();
}

/* Cierre de la conexión */
$mysqli->close();
?>




   &style.procedural;


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Verificación de la conexión */
if (mysqli_connect_errno()) {
    printf("Fallo en la conexión: %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Name LIMIT 5";

if ($result = mysqli_query($link, $query)) {

    /* Obtener información del campo para la columna 'SurfaceArea' */
    $finfo = mysqli_fetch_field_direct($result, 1);

    printf("Nombre      : %s\n", $finfo->name);
    printf("Tabla       : %s\n", $finfo->table);
    printf("Tamaño máx : %d\n", $finfo->max_length);
    printf("Flags       : %d\n", $finfo->flags);
    printf("Tipo        : %d\n", $finfo->type);

    mysqli_free_result($result);
}

/* Cierre de la conexión */
mysqli_close($link);
?>


   &examples.outputs;


Nombre      : SurfaceArea
Tabla       : Country
Tamaño máx : 10
Flags       : 32769
Tipo        : 4
