refentry.refsect1:
&reftitle.returnvalues;


 Un &integer; que representa el número de filas almacenadas en el búfer.
 Devuelve 0 en modo no almacenado en búfer, excepto si todas las filas han sido recuperadas del servidor.

&mysqli.integer.overflow.as.string.note;

  &reftitle.examples;

   &style.oop;


<?php

mysqli_report(MYSQLI_REPORT_ERROR | MYSQLI_REPORT_STRICT);
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

$query = "SELECT Name, CountryCode FROM City ORDER BY Name LIMIT 20";

$stmt = $mysqli->prepare($query);
$stmt->execute();

/* Almacenar el resultado en un búfer interno */
$stmt->store_result();

printf("Número de filas: %d.\n", $stmt->num_rows);
?>




   &style.procedural;


<?php

mysqli_report(MYSQLI_REPORT_ERROR | MYSQLI_REPORT_STRICT);
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

$query = "SELECT Name, CountryCode FROM City ORDER BY Name LIMIT 20";

$stmt = mysqli_prepare($link, $query);
mysqli_stmt_execute($stmt);

/* Almacenar el resultado en un búfer interno */
mysqli_stmt_store_result($stmt);

printf("Número de filas: %d.\n", mysqli_stmt_num_rows($stmt));
?>


   &examples.outputs;


Número de filas: 20.
